INSERT INTO `category_items` (`id`, `name`, `path`, `description`, `content`, `image`, `image_mobile`, `level`, `parent_id`, `type`, `order`, `isActive`, `created_at`, `updated_at`, `seo_id`) VALUES
(6, 'Dịch Vụ', 'dich-vu', '<p>\r\n	<span style=\"color:#ffffff;\">Tại Hera Center, sự kiện của bạn dẫn đầu xu hướng và lưu lại dấu ấn đỉnh cao cho từng thực khách trải nghiệm.</span>\r\n</p>', NULL, 'images/uploads/images/dichvu/banner-dich-vu-food-decor.jpg', NULL, 0, NULL, 0, 1, 1, '2018-09-17 08:05:17', '2018-09-27 01:13:41', 13),
(7, 'Thư Viện', 'thu-vien', NULL, NULL, 'images/uploads/images/thuvien/image014-2.jpg', NULL, 0, NULL, 0, 1, 1, '2018-09-17 08:08:34', '2018-10-04 16:17:23', 20),
(8, 'Thực Đơn', 'thuc-don', '<h6>\r\n	<span style=\"color:null;\"><strong><span style=\"font-size:24px;\">ẨM THỰC ÂU - Á KẾT HỢP</span></strong></span>\r\n</h6>\r\n\r\n<p>\r\n	<span style=\"color:null;\">Không dừng lại ở những món ăn thuần túy của dân tộc, Hera còn nghiên cứu về ẩm thực Châu Á lẫn Châu Âu để cho ra đời nhiều món ăn ngon &ndash; lạ &ndash; đẹp được kết hợp hết sức tinh tế giữa hai nền ẩm thực đặc sắc mà vẫn phù hợp với khẩu vị của người dân Việt Nam.</span>\r\n</p>', '<h5>\r\n	<span style=\"color:#21b6ff;\"><strong><span style=\"font-size:24px;\">MENU THAM KHẢO TẠI ĐÂY</span></strong></span>\r\n</h5>\r\n\r\n<p>\r\n	<span style=\"color:null;\">5 món mặn + 1 tráng miệng</span>\r\n</p>\r\n\r\n<p>\r\n	<span style=\"color:null;\">KHAI VỊ BA MÓN 700,000 VNĐ<br>\r\n	<br>\r\n	1 Nghêu Quay Giòn &ndash; Gỏi Mực Rau Câu &ndash; Sushi Thăn Heo Chiên<br>\r\n	2 Chả Giò Mayonnaise &ndash; Salad Nhật &ndash; Sushi Tôm<br>\r\n	3. Bacon Cuộn Cá &ndash; Salad Gà Chiên Giòn &ndash; Tôm Xù Cốm</span>\r\n</p>\r\n\r\n<p>\r\n	<span style=\"color:null;\">KHAI VỊ HAI MÓN<br>\r\n	<br>\r\n	1. Khai Vị Gà 2 Món 450,000 VNĐ<br>\r\n	Gà Sauce Chua Ngọt &ndash; Gà Teriyaki<br>\r\n	2. Khai Vị Tôm 2 Món 500,000 VNĐ<br>\r\n	Tôm Hạnh Nhân &ndash; Tôm Sauce Chanh<br>\r\n	3. Khai Vị Cá 600,000 VNĐ<br>\r\n	Cá Cuộn Nấm &ndash; Cá Hồi Chiên Giòn<br>\r\n	4. Khai Vị Hải Sản 2 Món 650,000 VNĐ<br>\r\n	Tôm Sauce Wasabi &ndash; Tôm Salad Trái Cây</span>\r\n</p>\r\n\r\n<p>\r\n	<span style=\"color:null;\">SOUP</span><br>\r\n	&nbsp;\r\n</p>\r\n\r\n<p>\r\n	<span style=\"color:null;\">* Soup Bông Cải 390,000 VNĐ<br>\r\n	* Soup Gà Nấm Tuyết 400,000 VNĐ<br>\r\n	* Soup Jambon Gà Xé 450,000 VNĐ<br>\r\n	* Soup Bóng Cá Tóc Tiên 450,000 VNĐ<br>\r\n	* Soup Hải Sản Tuyết Cua 500,000 VNĐ<br>\r\n	* Soup Măng Tây Cua 500,000 VNĐ<br>\r\n	* Soup Bò Thượng Hải 550,000 VNĐ<br>\r\n	* Soup Cá Hồi Hải Sản 700,000 VNĐ<br>\r\n	* Soup Nghêu Rong Biển Chua Cay 700,000 VNĐ<br>\r\n	* Soup Hải Sản Tứ Xuyên 750,000 VNĐ<br>\r\n	* Soup Cá Hồi HongKong 800,000 VNĐ<br>\r\n	* Soup Đuôi Bò Artichoke 866,000 VNĐ<br>\r\n	* Soup Bào Ngư,Vi Cá, Hải Sâm </span><span style=\"color:#ffffff;\">950,000 VNĐ</span>\r\n</p>', 'images/uploads/images/thucdon/menus-bg.jpg', NULL, 0, NULL, 0, 1, 1, '2018-09-18 03:32:20', '2018-10-04 16:12:24', 50),
(9, 'Ưu Đãi', 'uu-dai', NULL, NULL, 'images/uploads/images/tintuc/bg-title-uudai.jpg', NULL, 0, NULL, 0, 1, 1, '2018-09-18 04:00:03', '2018-10-02 15:56:33', 51),
(10, 'Tin Tức', 'tin-tuc', NULL, NULL, 'images/uploads/images/tintuc/bg-title-uudai.jpg', NULL, 0, NULL, 0, 1, 1, '2018-09-18 08:07:44', '2018-10-02 03:53:28', 57),
(11, 'Ưu Đãi Tiệc Cưới', 'uu-dai-tiec-cuoi', NULL, NULL, NULL, NULL, 0, 9, 0, 1, 1, '2018-10-02 04:41:08', '2018-10-02 04:41:08', 65),
(12, 'Ưu Đãi Hội Nghị', 'uu-dai-hoi-nghi', NULL, NULL, NULL, NULL, 1, 9, 0, 1, 1, '2018-10-02 04:41:29', '2018-10-02 04:41:29', 66);

