<?phpnamespace App\Mail;use App\Config;use Illuminate\Bus\Queueable;use Illuminate\Mail\Mailable;use Illuminate\Queue\SerializesModels;use Illuminate\Contracts\Queue\ShouldQueue;use Illuminate\Http\Request;class SendingPhoneToMe extends Mailable{    use Queueable, SerializesModels;    /**     * Create a new message instance.     *     * @return void     */    public function __construct()    {        //    }    /**     * Build the message.     *     * @return $this     */    public function build(Request $request)    {        $emailReceive = Config::where('name', 'email-receive')->first();        $subjectReceive = 'Số Điện Thoại Khách Hàng Cần Tư Vấn';        $fromReceive = Config::where('name', 'email-receive-from')->first();        return $this->view('mail.phone-to-me', ['phone' => $request->phone])->to($emailReceive->content)->subject($subjectReceive)->from('tieccuoiheracom@gmail.com', $fromReceive->content);;    }}